import React, { Component } from 'react';
import Users from './Users';
import AddUser from './AddUser';

class App extends Component{

    // Default dummy data
    state = {

        users:[
          {firstname:" Moorhouse", lastname: "varsha", age:22, isEditing:false},
          {name:"Jeffrey Delgado", firstname: "varsha", age:21, isEditing:false}

        ]
      }
      // (newUser) is received from AddUser.js
      addUser = (newUser) => {
            let users = [...this.state.users, newUser];
            this.setState({
                users
            });     
      }

      // when press edit button
      // (i) is received from Users.js
      pressEditBtn = (i) => {
        let users = this.state.users;
        users[i].isEditing = true;
        this.setState({
            users
        });
      }

      // (i, name, age) is received from Users.js
      updateUser = (i, firstname, _lastname, _email, _password, _username, age) => {
        let users = this.state.users;
        users[i].firstname = firstname;
        users[i]._lastname = _lastname;
        users[i]._email = _email;
        users[i]._password = _password;
        users[i]._username = _username;
        users[i].age = age;
        users[i].isEditing = false;

        this.setState({
            users
        });

      }
      // (i) is received from Users.js
      pressDelete = (i) => {
        let users = this.state.users.filter((u,index)=>{
            return index !== i;
        });
        this.setState({
            users
        });
      }

    render(){
        return(
            <div className="container">
                <h1>CRUD with React CLI</h1>
                <Users allUsers={this.state.users} pressEditBtn={this.pressEditBtn} updateUser={this.updateUser} pressDelete={this.pressDelete} />
                <AddUser addUser={this.addUser}/>
            </div>
        );
    }
}

export default App;
